mapper.select:
        SELECT


--         级联表的字段

        FROM yinyue  a



                and a.id in

                #{item}



                and a.yinyue_uuid_number like CONCAT('%',#{params.yinyueUuidNumber},'%')


                and a.yinyue_name like CONCAT('%',#{params.yinyueName},'%')


                and a.yinyue_music like CONCAT('%',#{params.yinyueMusic},'%')


                and a.yinyue_types = #{params.yinyueTypes}


                  and a.yinyue_clicknum >= #{params.yinyueClicknumStart}   


                  and a.yinyue_clicknum <= #{params.yinyueClicknumEnd}   


                and a.yinyue_clicknum = #{params.yinyueClicknum}


                  and a.zan_number >= #{params.zanNumberStart}   


                  and a.zan_number <= #{params.zanNumberEnd}   


                and a.zan_number = #{params.zanNumber}


                  and a.cai_number >= #{params.caiNumberStart}   


                  and a.cai_number <= #{params.caiNumberEnd}   


                and a.cai_number = #{params.caiNumber}


                and a.yinyue_content like CONCAT('%',#{params.yinyueContent},'%')


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.yinyue_delete >= #{params.yinyueDeleteStart}   


                  and a.yinyue_delete <= #{params.yinyueDeleteEnd}   


                and a.yinyue_delete = #{params.yinyueDelete}




        order by a.${params.orderBy} desc
